query GetBusiness($username: String!) {
  Business {
    read(username: $username) {
      username
      name {
        ar
        en
      }
      products {
        id
        name {
          ar
          en
        }
        prices {
          currency {
            name {
              ar
              en
            }
          }
          value
        }
        questions {
          name {
            ar
            en
          }
          type
          isRequired
          options {
            name
            changeInPrice
          }
          changeInPrice
        }
        imageUrl
        description {
          ar
          en
        }
        discountCodes {
          code
          amount
          discountType
          minimumTotal
          expiresAt
          createdAt
        }
      }
      brand {
        primaryColor
        secondaryColor
        logoUrl
      }
      orders {
        product {
          id
        }
        currency {
          name {
            ar
            en
          }
          code
        }
        language
        answers {
          question {
            id
          }
          answer
        }
        status
        customerPhoneNumber
        total
        id
      }
    }
  }
}
